mapper.select:
SELECT M.*, IF( (ROUND(AVG(S.point),1)-(ROUND(AVG(S.point),1)-0.5)) >= 0.5, ROUND(AVG(S.point)), ROUND(AVG(S.point))-0.5 ) AS extra__starPoint FROM `member` AS M LEFT JOIN starRating AS S ON S.relTypeCode = 'member' AND M.id = S.relId WHERE 1
AND M.authLevel = #{authLevel}
GROUP BY M.id ORDER BY M.id DESC

mapper.update:
UPDATE member


		updateDate = NOW()


		, loginPw = #{loginPw}


		, `name` = #{name}


		, nickname = #{nickname}


		, cellphoneNo = #{cellphoneNo}


		, email = #{email}


		, authLevel = #{authLevel}


		, `address` = #{address}


WHERE id = #{id}
