mapper.select:
SELECT M.* FROM `member` AS M WHERE 1
AND M.authLevel = #{authLevel}
ORDER BY M.id DESC

mapper.update:
UPDATE member


		updateDate = NOW()


		, loginPw = #{loginPw}


		, `name` = #{name}


		, nickname = #{nickname}


		, cellphoneNo = #{cellphoneNo}


		, email = #{email}


		, authLevel = #{authLevel}


		, `address` = #{address}


WHERE id = #{id}
